<?phpnamespace api\helpers;class RequestHandler {    public static function file($index) {        return (!empty($_FILES[$index])) ? $_FILES[$index] : null;    }    public static function post($index) {        if (!empty($_POST[$index]))            return (is_array($_POST[$index])) ? $_POST[$index] : $_POST[$index];        return false;    }    public static function get($index) {        if (!empty($_GET[$index]))            return (is_array($_GET[$index])) ? $_GET[$index] : $_GET[$index];        return false;    }    public static function decode($str) {        if (!empty($data))            return urldecode($str);        return $str;    }    public static function encode($str) {        if (!empty($data))            return urlencode($str);        return $str;    }}